import { useState } from "react";
import PartForm from "../forms/partsForm";
import ShippingForm from "../forms/shippingForm";

const Dashboard = ()=>{

    //Shipping
    const initialShippingValues= {
        PackingSLipID: "",
        ShippingDate: "",
        DeliveryDate: "",
        CarrierName: "",
        TrackingNo: "",
        PlantCode: "",
    };
    const [shipping, setShipping] = useState(initialShippingValues);

    const handleShippingChange = (e) =>{
        const { name, value }= e.target;
        setShipping({ ...shipping, [name]: value});
    }

    //Part
    const initialPart = {
        partNo: "",
        quantity: "",
        mixedOrMaster: "Mixed",
        repackedQuantity: "",
        customized:[]
      };
    
      const [parts, setParts] = useState([initialPart]);
    
      const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        const updatedParts = [...parts];
        updatedParts[index][name] = value;
    
         // Automatically set the "Repacked Quantity" to the same value as "Quantity"
         if (name === "quantity") {
            updatedParts[index].repackedQuantity = value;
          }
           // Check if both "Quantity" and "Repacked Quantity" have valid numeric values
            const quantity = parseFloat(part.quantity);
            const repackedQuantity = parseFloat(part.repackedQuantity);

            // Calculate and update the "Customized" array
                if (!isNaN(quantity) && !isNaN(repackedQuantity) && repackedQuantity !== 0) {
                const result = [];
                while (quantity >= repackedQuantity) {
                    result.push(repackedQuantity.toString());
                    quantity -= repackedQuantity;
                }
                if (quantity > 0) {
                    result.push(quantity.toString());
                }
                part.customized = result;
                } else {
                part.customized = [];
                }
         // Add a new row if the user is typing in the first field of the last row
         if (index === updatedParts.length - 1 && name === "partNo" && value.trim() !== "") {
            updatedParts.push({
                partNo: "",
                quantity: "",
                mixedOrMaster: "Mixed",
                repackedQuantity: "",
              });
          }
    
        setParts(updatedParts);
      };
    
      const handleRemovePart = (index) => {
        const updatedParts = [...parts];
    
        // Remove the row if the user clears the "Part No." field
        if (!updatedParts[index].partNo) {
          updatedParts.splice(index, 1);
        }
    
        setParts(updatedParts);
      };
    return(
        <div>
            <ShippingForm 
            shipping ={shipping}
            handleChange ={handleShippingChange}
            />
            <PartForm
            handleInputChange={handleInputChange}
            handleRemovePart={handleRemovePart}
            parts={parts} 
            />
        </div>
    )
};

export default Dashboard;
