import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchInventory } from '../../app/features/Inventory/inventoryAsyncThunk';

const Inventory = () => {
    const dispatch = useDispatch();
    const inventory = useSelector((state) => state.inventory.inventory);
    const status = useSelector((state) => state.inventory.status);
    const error = useSelector((state) => state.inventory.error);
    const [filters, setFilters] = useState({
        ItemCode: '',
        Batch: '',
        Warehouse: '',
        Bin: '',
        Quantity: '',
        'Ctn Count': '',
        PackSize: '',
        'Std Ctn Qty': '',
        Zone: '',
        LicensePlate: ''
    });

    useEffect(() => {
        dispatch(fetchInventory());
    }, [dispatch]);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters((prevFilters) => ({ ...prevFilters, [name]: value }));
    };

    const filteredInventory = inventory ? inventory.filter((part) =>
        Object.entries(filters).every(([key, value]) =>
            part[key].toString().toLowerCase().includes(value.toLowerCase())
        )
    ) : [];

    if (status === 'loading') {
        return <div>Loading...</div>;
    }

    if (status === 'failed') {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="container">
            <div className="row">
                {Object.keys(filters).map((filterKey) => (
                    <div className="col-md-2 mb-3" key={filterKey}>
                        <input
                            type="text"
                            className="form-control"
                            name={filterKey}
                            placeholder={filterKey}
                            value={filters[filterKey]}
                            onChange={handleFilterChange}
                        />
                    </div>
                ))}
            </div>
            <table className="table table-striped">
                <thead>
                    <tr>
                        {Object.keys(filters).map((header) => (
                            <th key={header}>{header}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {filteredInventory.map((part, index) => (
                        <tr key={index}>
                            {Object.keys(filters).map((key) => (
                                <td key={key}>{part[key]}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Inventory;
