import Dropdown from "../components/carrierDropdown";

const ShippingForm =(props)=>{
    const {PackingSLipID ,ShippingDate ,DeliveryDate ,CarrierName ,TrackingNo, PlantCode, Customer, ShipTo} = props.shipping;
    const formattedDate = shippingDate
    ? new Date(shippingDate).toLocaleDateString('en-US', {
        year: '2-digit',
        month: '2-digit',
        day: '2-digit',
      })
    : '';
    return(
        <div>
            <h2>Shipping Details</h2>
            <label htmlFor="PackingSLipIDInput">Packing Slip ID</label>
            <input 
            value={PackingSLipID}
            name="PackingSLipID"
            id="PackingSLipIDInput"
            onChange={props.handleChange}
            ></input>
            <div>
                <label htmlFor="ShippingDateInput">Shipping Date</label>
                <input
                    type="text"
                    value={formattedDate}
                    name="ShippingDate"
                    id="ShippingDateInput"
                    placeholder="MM/DD/YY"
                    pattern="\d{2}/\d{2}/\d{2}"
                    onChange={props.handleChange}
                />
                <input
                    type="date"
                    value={ShippingDate}
                    name="ShippingDate"
                    id="HiddenShippingDateInput"
                    onChange={props.handleChange}
                    style={{ display: 'none' }}
                />
            </div>
            <label htmlFor="DeliveryDateInput">Delivery Date</label>
            <input 
            type="date"
            value={DeliveryDate}
            name="DeliveryDate"
            id="DeliveryDateInput"
            onChange={props.handleChange}
            ></input>
            <label htmlFor="CarrierNameInput">Carrier Name</label>
            <Dropdown 
            value={CarrierName}
            name="CarrierName"
            id='CarrierNameInput'
            onChange={props.handleChange} />
            <label htmlFor="TrackingNoInput">Tracking No.</label>
            <input 
            value={TrackingNo}
            name="TrackingNo"
            id="TrackingNoInput"
            onChange={props.handleChange}
            ></input>
             <label htmlFor="PlantCodeInput">Plant Code</label>
            <input 
            value={PlantCode}
            name="PlantCode"
            id="PlantCodeInput"
            onChange={props.handleChange}
            ></input>
            <label htmlFor="CustomerInput">Customer</label>
            <input 
            value={Customer}
            name="Customer"
            id="CustomerInput"
            onChange={props.handleChange}
            ></input>
            <label htmlFor="ShipToInput">Ship To</label>
            <input 
            value={ShipTo}
            name="ShipTo"
            id="ShipToInput"
            onChange={props.handleChange}
            ></input>
        </div>
    )
};

export default ShippingForm;
