import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchCertificates } from '../../app/features/certicatesOfComplience/certificatesAsyncThunk';
import jsPDF from 'jspdf';

const CertificateOfCompliance = () => {
  const dispatch = useDispatch();
  const certificatesData = useSelector((state) => state.certificates.data);
  const [selectedCertificateIndex, setSelectedCertificateIndex] = useState(0);
  const [inputIndex, setInputIndex] = useState('');

  const handlePrintPDF = () => {
    const input = document.getElementById('pdf-content');
    const newWindow = window.open('', '_blank');
    newWindow.document.write(input.innerHTML);
    newWindow.print();
    newWindow.close();
  };

  const handleInputChange = (e) => {
    setInputIndex(e.target.value);
  };

  const handleDispatchFetch = () => {
    const index = Number(inputIndex);
    if (!isNaN(index) && index >= 0 && index < certificatesData.length) {
      setSelectedCertificateIndex(index);
      dispatch(fetchCertificates('270302')); // Use your appropriate argument
    } else {
      // Handle invalid index input
    }
  };

  if (!certificatesData || certificatesData.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <input
        type="text"
        placeholder="Enter Certificate Index"
        value={inputIndex}
        onChange={handleInputChange}
      />
      <button onClick={handleDispatchFetch}>Fetch Certificate</button>

      <div id="pdf-content">
        <div>
          <h2>Company Name: Shamrock International</h2>
          {/* ... (Include other details) */}
        </div>

        <div>
          <p>Invoice No: {certificatesData[selectedCertificateIndex]?.invoiceNumber}</p>
          <p>Pack Slip No: {certificatesData[selectedCertificateIndex]?.packSlipNumber}</p>
          <p>Customer: {certificatesData[selectedCertificateIndex]?.customer}</p>
          {/* ... (Include other details) */}
          <p>Final Audit Date: {certificatesData[selectedCertificateIndex]?.finalAuditDate}</p>
          <p>Country of Origin: {certificatesData[selectedCertificateIndex]?.countryOfOrigin}</p>
        </div>
      </div>

      <button onClick={handlePrintPDF}>Print PDF</button>
    </div>
  );
};

export default CertificateOfCompliance;
