import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchDailyOrders } from '../../app/features/orders/dailyOrdersAsyncthunk';// Update the path accordingly
import Button from 'react-bootstrap/Button';
import Overlay from 'react-bootstrap/Overlay';
import Popover from 'react-bootstrap/Popover';

const DailyOrders = () => {
  const dispatch = useDispatch();
  const orders = useSelector((state) => state.dailyOrders.data);
  const status = useSelector((state) => state.dailyOrders.status);
  const error = useSelector((state) => state.dailyOrders.error);

  useEffect(() => {
    // Dispatch the fetchDailyOrders action when the component mounts
    dispatch(fetchDailyOrders());
  }, [dispatch]);

  // Render loading state
  if (status === 'loading') {
    return <div>Loading...</div>;
  }

  // Render error state
  if (status === 'failed') {
    return <div>Error: {error}</div>;
  }

  const groupedOrders = {};
if (orders) {
  orders.forEach((order) => {
    const key = `${order.CardCode}-${order.ShipToCode}-${order.U_SIF_PO_Sample || 'N'}`;
    if (!groupedOrders[key]) {
      groupedOrders[key] = [];
    }
    groupedOrders[key].push(order);
  });
}

const [show, setShow] = useState(false);
  const [target, setTarget] = useState(null);
  const ref = useRef(null);

  const handleClick = (event) => {
    setShow(!show);
    setTarget(event.target);
  };


  // Render the component with fetched data
  return (
    <div >
      <h1>Daily Orders</h1>
      {orders && (
        <div>
          {Object.keys(groupedOrders).map((groupKey) => (
        <div key={groupKey} className='w-75 mx-auto d-flex flex-column align-items-center'>
          <div>
            <div className='d-flex justify-content-around'>
                <h4>{`${groupedOrders[groupKey][0].CardCode} - ${groupedOrders[groupKey][0].ShipToCode}`}</h4>
                <h5>{groupedOrders[groupKey][0].CardName}</h5>
                <h6>{groupedOrders[groupKey][0].U_SIF_PO_Sample || 'N'}</h6>
            </div>
            <p className='text-center ps-5'>{groupedOrders[groupKey][0].Address}</p>
            <Button onClick={handleClick}>Holy guacamole!</Button>

            <Overlay
            show={show}
            target={target}
            placement="bottom"
            container={ref}
            containerPadding={20}
            >
            <Popover id="popover-contained">
                <Popover.Header as="h3">Popover bottom</Popover.Header>
                <Popover.Body>
                <strong>Holy guacamole!</strong> Check this info.
                </Popover.Body>
            </Popover>
            </Overlay>
            <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content={`${groupedOrders[groupKey][0]['Ship Instruction Notes']}`}>Ship Instruction Notes</a>
            <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content={`${groupedOrders[groupKey][0]['BOL Notes']}`}>BOL Notes</a>
            <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content={`${groupedOrders[groupKey][0]['Weight Cut-off']}`}>Weight Cut-off</a>
            <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content={`${groupedOrders[groupKey][0]['Light-Weight Payment']}`}>Light-Weight Payment</a>
            <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content={`${groupedOrders[groupKey][0]['Heavy-Weight Payment']}`}>Heavy-Weight Payment</a>
            <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content={`${groupedOrders[groupKey][0]['Light-Weight Carrier']}`}>Light-Weight Carrier</a>
            <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content={`${groupedOrders[groupKey][0]['Light-Weight Carrier Acct']}`}>Light-Weight Carrier Acct</a>
            <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content={`${groupedOrders[groupKey][0]['LTL Carrier']}`}>LTL Carrier</a>
          </div>
          <table>
            <thead>
                <tr>
                <th>DocNum</th>
                <th>Cartons</th>
                <th>SKIDS</th>
                <th>Weight</th>
                <th>Part</th>
                <th>U_SIF_ShipInst1</th>
                <th>U_SIF_ShipInst2</th>
                </tr>
            </thead>
            <tbody>
                {groupedOrders[groupKey].map((order) => (
                <tr key={order.DocNum}>
                    <td>{order.DocNum}</td>
                    <td>{order.Cartons}</td>
                    <td>{order.SKIDS}</td>
                    <td>{order.Weight}</td>
                    <td>{order.PART}</td>
                    <td>{order.U_SIF_ShipInst1}</td>
                    <td>{order.U_SIF_ShipInst2}</td>
                </tr>
                ))}
                {/* Total row */}
                <tr>
                <td>Total</td>
                <td>{groupedOrders[groupKey].reduce((total, order) => total + order.Cartons, 0)}</td>
                <td>{groupedOrders[groupKey].reduce((total, order) => total + order.SKIDS, 0)}</td>
                <td>{groupedOrders[groupKey].reduce((total, order) => total + order.Weight, 0)}</td>
                <td>CARRIER: </td>
                <td>{groupedOrders[groupKey][0].CARRIER}</td>
                </tr>
            </tbody>
            </table>

        </div>
      ))}
        </div>
      )}
    </div>
  );
};

export default DailyOrders;
