import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchCertificates } from '../../app/features/certicatesOfComplience/certificatesAsyncThunk';
import jsPDF from 'jspdf';

const CertificateOfCompliance = () => {
  const dispatch = useDispatch();
  const certificatesData = useSelector((state) => state.certificates.data);
  const [selectedCertificateIndex, setSelectedCertificateIndex] = useState("");

  const handlePrintPDF = (certificate) => {
    const pdf = new jsPDF();
    pdf.text(`SO#: ${certificate.soNumber}`, 10, 10);
    // Add other details to the PDF as needed
    pdf.save(`certificate-of-compliance-${certificate.soNumber}.pdf`);
  };

  const handleDispatchFetch = () => {
    dispatch(fetchCertificates(selectedCertificateIndex)); // Update with your appropriate argument
  };

  if (!certificatesData || certificatesData.length === 0) {
    return <div>
        <input
        type="text"
        placeholder="Enter SO Number"
        value={selectedCertificateIndex}
        onChange={(value) =>{ setSelectedCertificateIndex(value.target.value)}}
      />
      <button onClick={handleDispatchFetch}>Fetch Certificates</button>
    </div>;
  }

  return (
    <div>
      <input
        type="text"
        placeholder="Enter SO Number"
        value={selectedCertificateIndex}
        onChange={(value) =>{ setSelectedCertificateIndex(value.target.value)}}
      />
      <button onClick={handleDispatchFetch}>Fetch Certificates</button>

      <div id="pdf-content">
        {certificatesData.map((certificate, index) => (
          <div key={index} style={{ marginBottom: '20px', border: '1px solid #000', padding: '20px' }}>
             <div>
                <h2>Company Name: Shamrock International</h2>
                <p>Address: 1475 East Industrial Drive Itasca, IL 60143</p>
                <p>Country: U.S.A</p>
                <h3>Certificate of Compliance</h3>
                <p>
                    THIS LETTER HEREBY CERTIFIES THAT THE LOT OF MATERIAL PROVIDED FOR THE BELOW MENTIONED PURCHASE ORDER COMPLIES IN ALL RESPECTS WITH ALL DIMENSIONS AND SPECIFICATIONS DESCRIBED ON YOUR PURCHASE ORDER AND PRINT.
                </p>
                </div>

                <div >
                <p>Invoice No: {certificate['Invoice Num']}</p>
                <p>Pack Slip No: {certificate.DocNum}</p>
                <p>Customer: {certificate.CardName}</p>
                <p>Purchase Order: {certificate['PO Number']}</p>  			
                <p>Customer PN:	{certificate.U_SIF_CustPN}</p>   		
                <p>Customer Rev:{certificate.Rev}</p>    				
                <p>Shamrock PN:	{certificate.ItemCode}</p>    			
                <p> Quantity: {certificate.Quantity}</p>  			
                <p>Lot Number: {certificate.BatchNumber}	</p>   			
                <p># of Cartons:{certificate.Ctns}</p>  		
                <p>Final Audit Date: {}</p>
                <p>Country of Origin: {certificate.COO}</p>
                </div>
            <button onClick={() => handlePrintPDF(certificate)}>Print PDF</button>
          </div>
        ))}
      </div>

      <div>
        <h2>Details for Selected Certificate</h2>
        {selectedCertificateIndex !== null && (
          <div style={{ border: '1px solid #000', padding: '20px' }}>
            <h2>Company Name: Shamrock International</h2>
            {/* ... (Include other details) */}
            <p>SO#: {certificatesData[selectedCertificateIndex]?.soNumber}</p>
            {/* ... (Include other details) */}
            <p>Invoice No: {certificatesData[selectedCertificateIndex]?.invoiceNumber}</p>
            <p>Pack Slip No: {certificatesData[selectedCertificateIndex]?.packSlipNumber}</p>
            <p>Customer: {certificatesData[selectedCertificateIndex]?.customer}</p>
            {/* ... (Include other details) */}
            <p>Final Audit Date: {certificatesData[selectedCertificateIndex]?.finalAuditDate}</p>
            <p>Country of Origin: {certificatesData[selectedCertificateIndex]?.countryOfOrigin}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CertificateOfCompliance;
