import React, { useState } from "react";

const PartForm = () => {
  const initialPart = {
    partNo: "",
    quantity: "",
    mixedOrMaster: "Mixed",
    repackedQuantity: "",
  };

  const [parts, setParts] = useState([initialPart]);

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const updatedParts = [...parts];
    updatedParts[index][name] = value;

     // Automatically set the "Repacked Quantity" to the same value as "Quantity"
     if (name === "quantity") {
        updatedParts[index].repackedQuantity = value;
      }  

     // Add a new row if the user is typing in the first field of the last row
     if (index === updatedParts.length - 1 && name === "partNo" && value.trim() !== "") {
        updatedParts.push(initialPart);
      }

    setParts(updatedParts);
  };

  const handleRemovePart = (index) => {
    const updatedParts = [...parts];

    // Remove the row if the user clears the "Part No." field
    if (!updatedParts[index].partNo) {
      updatedParts.splice(index, 1);
    }

    setParts(updatedParts);
  };

  return (
    <div>
      <h2>Part Form</h2>
      <table>
        <thead>
          <tr>
            <th>Part No.</th>
            <th>Quantity</th>
            <th>Mixed or Master</th>
            <th>Repacked Quantity</th>
          </tr>
        </thead>
        <tbody>
          {parts.map((part, index) => (
            <tr key={index}>
              <td>
                <input
                  type="text"
                  name="partNo"
                  value={part.partNo}
                  onChange={(e) => handleInputChange(e, index)}
                />
              </td>
              <td>
                <input
                  type="number"
                  name="quantity"
                  value={part.quantity}
                  onChange={(e) => handleInputChange(e, index)}
                />
              </td>
              <td>
              <select
                  name="mixedOrMaster"
                  value={part.mixedOrMaster}
                  onChange={(e) => handleInputChange(e, index)}
                >
                  <option value="Mixed">Mixed</option>
                  <option value="Master">Master</option>
                </select>
              </td>
              <td>
                <input
                  type="number"
                  name="repackedQuantity"
                  value={part.repackedQuantity}
                  onChange={(e) => handleInputChange(e, index)}
                />
              </td>
              <td>
                <button onClick={() => handleRemovePart(index)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PartForm;
