import Accordion from 'react-bootstrap/Accordion';

function AlwaysOpenAcordeon(props) {
  return (
    <Accordion defaultActiveKey={['0']} alwaysOpen>
      <Accordion.Item eventKey="0">
        <Accordion.Header>
            <div className='d-flex justify-content-around'>
                <h4>{`${props.groupedOrders.CardCode} - ${props.groupedOrders.ShipToCode}`}</h4>
                <h5>{props.groupedOrders.CardName}</h5>
                <h6>{props.groupedOrders.U_SIF_PO_Sample || 'N'}</h6>
            </div>
        </Accordion.Header>
        <Accordion.Body>
        <div>
            <p className='text-center ps-5'>{props.groupedOrders[0].Address}</p>
            <div className='d-flex justify-content-between'>
                {/* Render the last 8 items as New components */}
                {Object.keys(props.groupedOrders[0]).slice(-8).map((key, index) => (
                <PopoverDaily  key={index} data={props.groupedOrders[0][key]} title={key} />
                ))}
            </div>
            </div>
          <TableDaily groupedOrders={props.groupedOrders} /> 
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  );
}

export default AlwaysOpenAcordeon;
