import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchInventory } from '../../app/features/Inventory/inventoryAsyncThunk';

const Inventory = () => {
    const dispatch = useDispatch();
    const inventory = useSelector((state) => state.inventory.inventory);
    const status = useSelector((state) => state.inventory.status);
    const error = useSelector((state) => state.inventory.error);
    const [filters, setFilters] = useState({
        ItemCode: '',
        Batch: '',
        Warehouse: '',
        Bin: '',
        Quantity: '',
        'Ctn Count': '',
        PackSize: '',
        'Std Ctn Qty': '',
        Zone: '',
        LicensePlate: ''
    });
    const [inputValues, setInputValues] = useState({ ...filters });

    useEffect(() => {
        dispatch(fetchInventory());
    }, [dispatch]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setInputValues((prevValues) => ({ ...prevValues, [name]: value }));
    };

    const handleApplyFilters = () => {
        setFilters(inputValues);
    };

    const filteredInventory = inventory ? inventory.filter((part) =>
        Object.entries(filters).every(([key, value]) =>
            (part[key] === null ? '' : part[key].toString()).toLowerCase().includes(value.toLowerCase())
        )
    ) : [];

    if (status === 'loading') {
        return <div>Loading...</div>;
    }

    if (status === 'failed') {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="container">
            <div className="row">
                {Object.keys(inputValues).map((filterKey) => (
                    <div className="col-md-2 mb-3" key={filterKey}>
                        <select
                            className="form-select"
                            name={filterKey}
                            value={inputValues[filterKey]}
                            onChange={handleInputChange}
                        >
                            <option value="">Select {filterKey}</option>
                            {[...new Set(inventory.map((item) => item[filterKey]))].map((option) => (
                                <option key={option} value={option}>
                                    {option}
                                </option>
                            ))}
                        </select>
                    </div>
                ))}
                <div className="col-md-2 mb-3">
                    <button className="btn btn-primary" onClick={handleApplyFilters}>
                        Apply Filters
                    </button>
                </div>
            </div>
            <table className="table table-striped">
                <thead>
                    <tr>
                        {Object.keys(filters).map((header) => (
                            <th key={header}>{header}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {filteredInventory.map((part, index) => (
                        <tr key={index}>
                            {Object.keys(filters).map((key) => (
                                <td key={key}>{part[key]}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Inventory;
