import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchInventory, fetchInventoryConsolidation, fetchInventoryLocked } from '../../app/features/Inventory/inventoryAsyncThunk';
import Select from 'react-select';
import { Tab, Tabs } from 'react-bootstrap';

const Inventory = () => {
    const dispatch = useDispatch();
    const inventory = useSelector((state) => state.inventory.inventory);
    const consolidation = useSelector((state) => state.inventory.consolidation);
    const lockedInventory = useSelector((state) => state.inventory.lockedInventory);
    const inventoryStatus = useSelector((state) => state.inventory.inventoryStatus);
    const consolidationStatus = useSelector((state) => state.inventory.consolidationStatus);
    const lockedInventoryStatus = useSelector((state) => state.inventory.lockedInventoryStatus);
    const error = useSelector((state) => state.inventory.error);
    const [activeTab, setActiveTab] = useState('inventory');
    const [inputValues, setInputValues] = useState({
        inventory: { ...initialInventoryFilters },
        consolidation: { ...initialConsolidationFilters },
        lockedInventory: { ...initialLockedInventoryFilters }
    });
    const [filters, setFilters] = useState({
        inventory: { ...initialInventoryFilters },
        consolidation: { ...initialConsolidationFilters },
        lockedInventory: { ...initialLockedInventoryFilters }
    });

    useEffect(() => {
        dispatch(fetchInventory());
        dispatch(fetchInventoryConsolidation());
        dispatch(fetchInventoryLocked());
    }, [dispatch]);

    const handleInputChange = (filterKey, value) => {
        setInputValues((prevValues) => ({
            ...prevValues,
            [activeTab]: {
                ...prevValues[activeTab],
                [filterKey]: value
            }
        }));
    };

    const applyFilters = () => {
        setFilters((prevFilters) => ({
            ...prevFilters,
            [activeTab]: inputValues[activeTab]
        }));
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            applyFilters();
        }
    };

    const getFilteredData = (data, filters) => {
        return data ? data.filter((part) =>
            Object.entries(filters).every(([key, value]) =>
                value === '' || (part[key] === null ? '' : part[key].toString()).toLowerCase() === value.toLowerCase()
            )
        ) : [];
    };

    const filteredInventory = getFilteredData(inventory, filters.inventory);
    const filteredConsolidation = getFilteredData(consolidation, filters.consolidation);
    const filteredLockedInventory = getFilteredData(lockedInventory, filters.lockedInventory);

    if (inventoryStatus === 'loading') {
        return <div>Loading...</div>;
    }

    if (inventoryStatus === 'failed') {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="container">
            <Tabs activeKey={activeTab} onSelect={(k) => setActiveTab(k)} className="mb-3">
                <Tab eventKey="inventory" title="Inventory">
                    {renderTabContent(filteredInventory, Object.keys(filters.inventory), 'inventory')}
                </Tab>
                <Tab eventKey="consolidation" title="Consolidation">
                    {renderTabContent(filteredConsolidation, Object.keys(filters.consolidation), 'consolidation')}
                </Tab>
                <Tab eventKey="lockedInventory" title="Locked Inventory">
                    {renderTabContent(filteredLockedInventory, Object.keys(filters.lockedInventory), 'lockedInventory')}
                </Tab>
            </Tabs>
            <button onClick={applyFilters} className="btn btn-primary mb-3">Filter</button>
        </div>
    );

    function renderTabContent(data, filterKeys, tabName) {
        return (
            <>
                <div className="row">
                    {filterKeys.map((filterKey) => (
                        <div className="col-md-2 mb-3" key={filterKey}>
                            <Select
                                options={data ? [...new Set(data.map((item) => item[filterKey]))].map((option) => ({ label: String(option), value: option })) : []}
                                onChange={(selectedOption) => handleInputChange(filterKey, selectedOption ? selectedOption.value
                                    : '')}
                                    placeholder={`Select ${filterKey}`}
                                    isClearable
                                    onKeyDown={handleKeyPress}
                                    filterOption={(option, input) => {
                                        if (option.label === null || option.label === undefined) return false;
                                        return String(option.label).toLowerCase().startsWith(input.toLowerCase());
                                    }}
                                />
                            </div>
                        ))}
                    </div>
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                {filterKeys.map((header) => (
                                    <th key={header}>{header}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((part, index) => (
                                <tr key={index}>
                                    {filterKeys.map((key) => (
                                        <td key={key}>{part[key]}</td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            );
        }
    };
    
    export default Inventory;
