import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchInventory, fetchInventoryConsolidation, fetchInventoryLocked } from '../../app/features/Inventory/inventoryAsyncThunk';
import Select from 'react-select';
import { Tab, Tabs } from 'react-bootstrap';

const Inventory = () => {
    const dispatch = useDispatch();
    const inventory = useSelector((state) => state.inventory.inventory);
    const consolidation = useSelector((state) => state.inventory.consolidation);
    const lockedInventory = useSelector((state) => state.inventory.lockedInventory);
    const inventoryStatus = useSelector((state) => state.inventory.inventoryStatus);
    const consolidationStatus = useSelector((state) => state.inventory.consolidationStatus);
    const lockedInventoryStatus = useSelector((state) => state.inventory.lockedInventoryStatus);
    const error = useSelector((state) => state.inventory.error);
    const [activeTab, setActiveTab] = useState('inventory');
    const [inventoryFilters, setInventoryFilters] = useState({
        ItemCode: '',
        Batch: '',
        Warehouse: '',
        Bin: '',
        Quantity: '',
        'Ctn Count': '',
        PackSize: '',
        'Std Ctn Qty': '',
        Zone: '',
        LicensePlate: ''
    });
    const [consolidationFilters, setConsolidationFilters] = useState({
        Bin: '',
        'Ctn Count': '',
        'Item Count': '',
        '# Batches': '',
        Zone: '',
        Spaces: '',
        Status: ''
    });
    const [lockedInventoryFilters, setLockedInventoryFilters] = useState({
        ItemCode: '',
        Quantity: '',
        'Lock Type': '',
        'Customer/Picklist #': '',
        BatchNumber: '',
        Location: ''
    });

    useEffect(() => {
        dispatch(fetchInventory());
        dispatch(fetchInventoryConsolidation());
        dispatch(fetchInventoryLocked());
    }, [dispatch]);

    const handleInputChange = (name, value) => {
        switch (activeTab) {
            case 'inventory':
                setInventoryFilters(prev => ({ ...prev, [name]: value }));
                break;
            case 'consolidation':
                setConsolidationFilters(prev => ({ ...prev, [name]: value }));
                break;
            case 'lockedInventory':
                setLockedInventoryFilters(prev => ({ ...prev, [name]: value }));
                break;
            default:
                break;
        }
    };

    const getFilteredData = (data, filters) => {
        return data ? data.filter((part) =>
            Object.entries(filters).every(([key, value]) =>
                (part[key] === null ? '' : part[key].toString()).toLowerCase().includes(value.toLowerCase())
            )
        ) : [];
    };

    const filteredInventory = getFilteredData(inventory, inventoryFilters);
    const filteredConsolidation = getFilteredData(consolidation, consolidationFilters);
    const filteredLockedInventory = getFilteredData(lockedInventory, lockedInventoryFilters);

    if (inventoryStatus === 'loading') {
        return <div>Loading...</div>;
    }

    if (inventoryStatus === 'failed') {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="container">
            <Tabs activeKey={activeTab} onSelect={(k) => setActiveTab(k)} className="mb-3">
                <Tab eventKey="inventory" title="Inventory">
                    {inventoryStatus === 'loading' ? <div>Loading Inventory...</div> : renderTabContent(filteredInventory, Object.keys(inventoryFilters))}
                </Tab>
                <Tab eventKey="consolidation" title="Consolidation">
                    {consolidationStatus === 'loading' ? <div>Loading Consolidation...</div> : renderTabContent(filteredConsolidation, Object.keys(consolidationFilters))}
                </Tab>
                <Tab eventKey="lockedInventory" title="Locked Inventory">
                    {lockedInventoryStatus === 'loading' ? <div>Loading Locked Inventory...</div> : renderTabContent(filteredLockedInventory, Object.keys(lockedInventoryFilters))}
                </Tab>
            </Tabs>
        </div>
    );

        function renderTabContent(data, filterKeys) {
        return (
            <>
                <div className="row">
                {filterKeys.map((filterKey) => (
                    <div className="col-md-2 mb-3" key={filterKey}>
                        <Select
                            options={data ? [...new Set(data.map((item) => item[filterKey]))].map((option) => ({ label: String(option), value: option })) : []}
                            onChange={(selectedOption) => handleInputChange(filterKey, selectedOption ? selectedOption.value : '')}
                            placeholder={`Select ${filterKey}`}
                            isClearable
                            filterOption={(option, input) => option.label.toLowerCase().startsWith(input.toLowerCase())}
                        />
                    </div>
                ))}

                </div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            {filterKeys.map((header) => (
                                <th key={header}>{header}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((part, index) => (
                            <tr key={index}>
                                {filterKeys.map((key) => (
                                    <td key={key}>{part[key]}</td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </>
        );
    }
};

export default Inventory;
