import { createSlice } from '@reduxjs/toolkit';
import { fetchDailyOrders } from './dailyOrdersAsyncthunk';

const dailyOrdersSlice = createSlice({
  name: 'dailyOrders',
  initialState: {
    data: null,
    status: 'idle',
    error: null,
    editableOrders: [],
  },
  reducers: {
    // Add other reducers if needed
    setEditableOrder: (state, action) => {
        const { key, value } = action.payload;
        const orderIndex = state.editableOrders.findIndex((order) => order.key === key);
  
        if (orderIndex === -1) {
          state.editableOrders.push({ key, value });
        } else {
          state.editableOrders[orderIndex].value = value;
        }
      },
      resetEditableOrders: (state) => {
        state.editableOrders = [];
      },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchDailyOrders.pending, (state) => {
      state.status = 'loading';
    });
    builder.addCase(fetchDailyOrders.fulfilled, (state, action) => {
      
      state.status = 'succeeded';
      state.data = action.payload;
    });
    builder.addCase(fetchDailyOrders.rejected, (state, action) => {
      state.status = 'failed';
      state.error = action.error.message;
    });
  },
});

export default dailyOrdersSlice.reducer;
