import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchCertificates } from '../../app/features/certicatesOfComplience/certificatesAsyncThunk';
import jsPDF from 'jspdf';

const CertificateOfCompliance = () => {
  const dispatch = useDispatch();
  const certificatesData = useSelector((state) => state.certificates.data);
  const [selectedCertificateIndex, setSelectedCertificateIndex] = useState("");

  const handlePrintPDF = () => {
    const pdf = new jsPDF();
  
    certificatesData.forEach((certificate, index) => {
      if (index > 0) {
        pdf.addPage(); // Add a new page for each certificate after the first one
      }
  
      // Certificate details
      pdf.text(`Company Name: Shamrock International`, 10, 10);
      pdf.text(`Address: 1475 East Industrial Drive Itasca, IL 60143`, 10, 20);
      pdf.text(`Country: U.S.A`, 10, 30);
      pdf.text(`Certificate of Compliance`, 10, 40);
      pdf.text(`THIS LETTER HEREBY CERTIFIES THAT THE LOT OF MATERIAL PROVIDED FOR THE BELOW MENTIONED PURCHASE ORDER COMPLIES IN ALL RESPECTS WITH ALL DIMENSIONS AND SPECIFICATIONS DESCRIBED ON YOUR PURCHASE ORDER AND PRINT.`, 10, 50);
  
      pdf.text(`Invoice No: ${certificate['Invoice Num']}`, 10, 60);
      pdf.text(`Pack Slip No: ${certificate.DocNum}`, 10, 70);
      pdf.text(`Customer: ${certificate.CardName}`, 10, 80);
      pdf.text(`Purchase Order: ${certificate['PO Number']}`, 10, 90);
      pdf.text(`Customer PN: ${certificate.U_SIF_CustPN}`, 10, 100);
      pdf.text(`Customer Rev: ${certificate.Rev}`, 10, 110);
      pdf.text(`Shamrock PN: ${certificate.ItemCode}`, 10, 120);
      pdf.text(`Quantity: ${certificate.Quantity}`, 10, 130);
      pdf.text(`Lot Number: ${certificate.BatchNumber}`, 10, 140);
      pdf.text(`# of Cartons: ${certificate.Ctns}`, 10, 150);
      pdf.text(`Final Audit Date: ${certificate['Final Audit Date']}`, 10, 160);
      pdf.text(`Country of Origin: ${certificate.COO}`, 10, 170);
    });
  
    // Save the PDF with a single file name
    pdf.save('certificates-of-compliance.pdf');
  };
  

  const handleDispatchFetch = () => {
    dispatch(fetchCertificates(selectedCertificateIndex)); // Update with your appropriate argument
  };

  if (!certificatesData || certificatesData.length === 0) {
    return <div>
        <input
        type="text"
        placeholder="Enter SO Number"
        value={selectedCertificateIndex}
        onChange={(value) =>{ setSelectedCertificateIndex(value.target.value)}}
      />
      <button onClick={handleDispatchFetch}>Fetch Certificates</button>
    </div>;
  }

  return (
    <div>
      <input
        type="text"
        placeholder="Enter SO Number"
        value={selectedCertificateIndex}
        onChange={(value) =>{ setSelectedCertificateIndex(value.target.value)}}
      />
      <button onClick={handleDispatchFetch}>Fetch Certificates</button>

      <div id="pdf-content">
        {certificatesData.map((certificate, index) => (
          <div key={index} style={{ marginBottom: '20px', border: '1px solid #000', padding: '20px' }}>
             <div>
                <h2>Company Name: Shamrock International</h2>
                <p>Address: 1475 East Industrial Drive Itasca, IL 60143</p>
                <p>Country: U.S.A</p>
                <h3>Certificate of Compliance</h3>
                <p>
                    THIS LETTER HEREBY CERTIFIES THAT THE LOT OF MATERIAL PROVIDED FOR THE BELOW MENTIONED PURCHASE ORDER COMPLIES IN ALL RESPECTS WITH ALL DIMENSIONS AND SPECIFICATIONS DESCRIBED ON YOUR PURCHASE ORDER AND PRINT.
                </p>
                </div>

                <div >
                <p>Invoice No: {certificate['Invoice Num']}</p>
                <p>Pack Slip No: {certificate.DocNum}</p>
                <p>Customer: {certificate.CardName}</p>
                <p>Purchase Order: {certificate['PO Number']}</p>  			
                <p>Customer PN:	{certificate.U_SIF_CustPN}</p>   		
                <p>Customer Rev:{certificate.Rev}</p>    				
                <p>Shamrock PN:	{certificate.ItemCode}</p>    			
                <p> Quantity: {certificate.Quantity}</p>  			
                <p>Lot Number: {certificate.BatchNumber}	</p>   			
                <p># of Cartons:{certificate.Ctns}</p>  		
                <p>Final Audit Date: {}</p>
                <p>Country of Origin: {certificate.COO}</p>
                </div>
                </div>

        ))}
      </div>
      <button onClick={handlePrintPDF}>Print All Certificates</button>
    </div>
  );
};

export default CertificateOfCompliance;
